// ==UserScript==
// @name        rutorrentlinksender
// @namespace   ansancho
// @description Script to send links to your rutorrent seedbox
// @description based heavily on http://userscripts-mirror.org/scripts/show/149711 by  auspexpt
// @version     1
// @require  http://code.jquery.com/jquery-1.7.2.min.js
// @match https://what.cd/*
// @match https://broadcasthe.net/*
// @match https://tls.passthepopcorn.me/*
// @grant GM_xmlhttpRequest
// ==/UserScript==

$(document).ready(function () {
	if (unsafeWindow.console) {
		var GM_log = unsafeWindow.console.log;
	}
	
//URL of your rutorrent server.

	var rutorrent_url = "https://my.server.com"; 
	//if credentials are stored in the browser it will ask for them.
	//var username = "xxx";
	//var password = "xxx";
	var start_stopped = true;

//Regex of torrent links.

	var sites = {
		'whatcd' : 'what\\.cd/torrents\\.php\\?action=download\\&id',		
		'ptp' : 'tls\\.passthepopcorn\\.me/torrents\\.php\\?action=download\\&id=',		
		'btn' : 'broadcasthe\\.net/torrents\\.php\\?action=download\\&id=',		
	};
	
// Site's Cookies. For now, on ptp and w.cd are not necessary, because auth goes on GET petitions.

	var cookies = {
	    'btn'	: {
	    	'PHPSESSID': null, '__cfduid' : 'your_value',
	    	'keeplogged':'your_value'
	    },	
	};

// Do not edit below this line

	var images = {
		'base': 'data:image/png;base64,',
		'ok': 'data:image/png;base64,',
		'nok': 'data:image/png;base64,',
		'loading':'data:image/png;base64,',
	};


	$('a').each(function (index) {
		var href = $(this).prop("href");
		var validLink = checkLink(href);
		if (validLink) {
			$(this).parent().append($("<a>")
				.css({verticalAlign: 'middle', lineHeight: '20px', marginLeft: '5px'})
				.attr("title", 'Download using ruTorrent WebUI')
				.attr("href", "#")
				.append($("<img>")
					.attr("src", images['base'])
					.css({border: 'none',})
					.click(function (event) {
						$(event.target).attr("src", images['loading']);
						event.preventDefault();
						GM_xmlhttpRequest({
							method: "GET",
							url: validLink,
							onload: function (response) {
								if(response.responseText.match(/success/gi)){
									$(event.target).attr("src", images['ok']);
								}else{
									$(event.target).attr("src", images['nok']);
									$(event.target).attr("title", "Error while sending torrent to ruTorrent: " + response.responseText);
								}
							},
							onerror: function (response) {
								$(event.target).attr("src", images['nok']);
								$(event.target).attr("title", "Error while sending torrent to ruTorrent: " + response.responseText);
							}
						});
					})
				)
			);
		}
	});


	function checkLink(link) {
		var matched = false;
		var sitefound = false;
		for (var site in sites) {
			var regex = new RegExp(sites[site]);
			if (link.match(regex)) {
				matched = link;
				console.log("URL %s matched regex %s!", link, regex);
				sitefound = site;
				break;
			}
		}
		if (matched) {
			var url = rutorrent_url + "/php/addtorrent.php?url=" + encodeURIComponent(link);
			url+=getCookies(sitefound);
			if (start_stopped) url += "&torrents_start_stopped=1";
			//console.log("Cookies for site %s are: %s", sitefound, getCookies(sitefound));
			console.log("Full url: " + url);
			return url;
		} else {
			return false;
		}
	}


	function getCookies(site) {

		var names = cookies[site];
		if (!names) return '';
		var sitecookies = {};
		var exist = false;
		for (var name in names) {
			var value = names[name];
			if (!value) value = readCookie(name);
			if (value) {
				sitecookies[name] = value;
				exist = true;
			}

		}
		if (!exist) return '';
		var first = true;
		var result = ':COOKIE:';
		for (var name in sitecookies) {
			var value = sitecookies[name];
			var cookie = name + '=' + value;
			if (!first) result += ';';
			result += cookie;
			first = false;
		}
		return result;
	}

	function readCookie(name) {
		name += '=';
		var ca = document.cookie.split(';');
		for (var i = 0; i < ca.length; i++) {
			var c = ca[i];
			while (c.charAt(0) == ' ') c = c.substring(1, c.length);
			if (c.indexOf(name) == 0) return c.substring(name.length, c.length);
		}
		return null;
	}

});
